package javaprogram;

public class Inbuiltmethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="swetha";
		String s2="andrews";
		String s3=s1+s2;
		String s4=s1.concat(s2);
		System.out.println("concat using + operator" +s3);
		System.out.println("concat using concat method" +s4);
		

	}

}
